function isTimeToday(a){var b=new Date,c=new Date(a),d=864e5;return c.getTime()>b.getTime()&&c.getTime()<b.getTime()+d}function createTime(a){var b=new Date,c=new Date(a),d="";return c.getDate()==b.getDate()?d+="Today ":c.getDate()==b.getDate()+1&&(d+="Tomorrow "),d+=c.getHours()+":00"}function addTime(a){new Date(a.validTime);if(okToPlay(a.ws,a.gust)){var b=document.getElementById("times");b.innerHTML+="<p>"+createTime(a.validTime)+": "+a.ws+" ("+a.gust+")</p>"}}function okToPlay(a,b){return MAX_WS>=a&&MAX_GUST>=b}function getData(){{var a="http://opendata-download-metfcst.smhi.se/api/category/pmp1.5g/version/1/geopoint/lat/57.42/lon/11.95/data.json";$.getJSON(a,function(a){$.each(a,function(a,b){if("referenceTime"==a);else if("timeseries"==a)for(var c in b){var d=(b[c].ws,b[c].gust,b[c].validTime);isTimeToday(d)&&addTime(b[c])}})})}}console.log("'Allo 'Allo!");var MAX_WS=5,MAX_GUST=10;